import React from 'react'
import { Link as RouterLink } from "react-router-dom";
import Moment from 'react-moment';
import 'moment-timezone';
import { makeStyles } from '@material-ui/core/styles';
import EditIcon from '@material-ui/icons/Edit';
import DeleteIcon from '@material-ui/icons/Delete';
import Button from '@material-ui/core/Button';
import Icon from '@material-ui/core/Icon';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';

const LinkEdit = React.forwardRef((props,ref) => <RouterLink innerRef={ref} {...props} />);

const ShowTip=(props)=>{
  const [tip, setTip] = React.useState({});

  const useStyles = makeStyles(theme => ({
    button: {
      margin: theme.spacing(1),
    },
    card: {
    minWidth: 275,
    maxWidth: 380,

  },
    bullet: {
      display: 'flex',
      margin: '10',
      transform: 'scale(0.8)',
    },
    title: {
      fontSize: 14,
    },
    pos: {
      marginBottom: 12,
    },
  }));

  const classes = useStyles();
  const bull = <span className={classes.bullet}>â€¢</span>;

  React.useEffect(()=>{
    const id = props.match.params.id;
    fetch("http://localhost:8080/tip/"+id)
      .then((res)=>res.json())
      .then((response)=>{
          setTip(response);
      })
  }, [])

  const deleteTip=(id)=>{
    fetch("http://localhost:8080/tip/"+id, {
      method:'delete'
    }).then(()=>{
      props.fetchTips();
      props.history.push('/tips');
    })
  }
  return(
    <div>
    <Card className={classes.card}>
      <CardContent>
        <Typography className={classes.title} color="textSecondary" gutterBottom>
          Date: <Moment format="MMM D, YYYY" withTitle>{tip.date}</Moment>
        </Typography>
        <Typography variant="h5" component="h2">
         Date: <Moment format="MMM D, YYYY" withTitle>{tip.date}</Moment>
         <br />
         <br />
        </Typography>
        <Typography className={classes.pos} color="textSecondary">
          Tip Amount: ${tip.tipAmount}
        </Typography>
        <Typography className={classes.pos} color="textSecondary">
          Amount Saved: ${tip.savingsRate}
        </Typography>
        <Typography className={classes.pos} color="textSecondary">
          Amount Saved: ${tip.amountSaved}
        </Typography>
      </CardContent>
      <CardActions>
      <Button
        component={LinkEdit}
        to={"/edit/tip/" + tip.id}
        variant="contained"
        color="primary"
        className={classes.button}
        startIcon={<EditIcon />}
      >
        Edit
      </Button>
      <Button
        variant="contained"
        onClick={()=>deleteTip(tip.id)}
        color="secondary"
        className={classes.button}
        startIcon={<DeleteIcon />}
      >
        Delete
      </Button>
      </CardActions>
    </Card>
    </div>
  )
}

export default ShowTip
