import React from 'react';
import './App.css';
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import Home from "./components/Home"
import ShowTips from "./components/ShowTips"
import ShowTip from "./components/ShowTip"
import TipForm from "./components/TipForm"
import AppBar from '@material-ui/core/AppBar';

const styles = {
    header: {
        backgroundImage: `url(${"https://o.aolcdn.com/images/dims?quality=85&image_uri=https%3A%2F%2Fo.aolcdn.com%2Fimages%2Fdims%3Fresize%3D2000%252C2000%252Cshrink%26image_uri%3Dhttps%253A%252F%252Fs.yimg.com%252Fos%252Fcreatr-uploaded-images%252F2019-07%252F5cf0c5d0-b1de-11e9-96b7-cedf7a3d0dcd%26client%3Da1acac3e1b3290917d92%26signature%3De99e2cce5e94763a3dc0508b531dea5230c0b044&client=amp-blogside-v2&signature=ee93bf27f9aa5ad45ae060f1f31fc454af0ae511"})`,
        height: auto
    }
};

class App extends React.Component {
  constructor(){
    super()
    this.state={
      tips:[]
    }
  }
  fetchTips=()=>{
    fetch("http://localhost:8080/tips")
      .then((res)=>res.json())
      .then((response)=>{
        this.setState({tips:response})
    })
  }
  componentDidMount(){
    this.fetchTips();
  }
  render(){
    return(
    <div>
      <AppBar style={styles.header} position="static">
        <h1>My header</h1>
      </AppBar>


      <Router>
        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/tips">View Tips</Link>
            </li>
            <li>
              <Link to="/create">Add Tips</Link>
            </li>
          </ul>
        </nav>
        <div id="content_body">
          <Switch>

          <Route path='/tip/:id' render={(props)=>(
            <ShowTip {...props} fetchTips={this.fetchTips}/>
          )} />

          <Route path='/edit/tip/:id' render={(props)=>(
            <TipForm {...props} fetchTips={this.fetchTips}/>
          )} />

          <Route path='/create' render={(props)=>(
            <TipForm {...props} fetchTips={this.fetchTips}/>
          )} />

            <Route exact path='/'>
              <Home />
            </Route>

            <Route path='/tips'>
              <ShowTips tips={this.state.tips}/>
            </Route>

          </Switch>
        </div>
      </Router>
      </div>
    );
  }
}

export default App;
