import React from 'react';
import './App.css';
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import Home from "./components/Home"
import ShowTips from "./components/ShowTips"
import CreateTips from "./components/CreateTips"

class App extends React.Component {
  constructor(){
    super()
    this.state={
      tips:[]
    }
  }
  fetchTips=()=>{
    fetch("http://localhost:8080/tips")
      .then((res)=>res.json())
      .then((response)=>{
        this.setState({tips:response})
    })
  }
  componentDidMount(){
    this.fetchTips();
  }
  render(){
    return(
      <Router>
        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/tips">View Tips</Link>
            </li>
            <li>
              <Link to="/create">Add Tips</Link>
            </li>
          </ul>
        </nav>
        <div id="content_body">
          <Switch>
            <Route exact path='/'>
              <Home />
            </Route>

            <Route path='/tips'>
              <ShowTips />
            </Route>

            <Route exact path='/create'>
              <CreateTips />
            </Route>

          </Switch>
        </div>
      </Router>
    );
  }
}

export default App;
